"use strict";M.AutoInit();let userHasScrolled=!1;const colorNavButton=()=>{let buttonToChange="";switch(window.location.href.substr(window.location.href.length-10)){case"index.html":buttonToChange=document.getElementsByClassName("nav-home");break;case"about.html":buttonToChange=document.getElementsByClassName("nav-about");break;case"jects.html":buttonToChange=document.getElementsByClassName("nav-projects");break;case"links.html":buttonToChange=document.getElementsByClassName("nav-links")}Array.from(buttonToChange).forEach(button=>{button.classList.remove("teal","lighten-1"),button.classList.add("blue-grey","darken-2")})},randomizeColors=()=>{Array.from(document.getElementsByClassName("random-color")).forEach(item=>{let newColor=[];for(let i=0;i<3;i++)newColor.push(Math.floor(192*Math.random())+64);const colorString=`rgb(${newColor[0]}, ${newColor[1]}, ${newColor[2]})`;item.style.color=colorString})},goToTop=()=>{scroll(0,0);const btntop=document.getElementById("btn-top");btntop.classList.remove("show"),btntop.classList.add("hide"),window.setTimeout(()=>{userHasScrolled=!1},500)};window.onload=(()=>{colorNavButton(),Array.from(document.getElementsByClassName("random-color")).forEach(item=>{let newColor=[];for(let i=0;i<3;i++)newColor.push(Math.floor(192*Math.random())+64);const colorString=`rgb(${newColor[0]}, ${newColor[1]}, ${newColor[2]})`;item.style.color=colorString})}),window.addEventListener("scroll",()=>{if(!userHasScrolled){userHasScrolled=!0;const btntop=document.getElementById("btn-top");btntop.classList.remove("hide"),btntop.classList.add("show","pulse"),window.setTimeout(()=>{btntop.classList.remove("pulse")},3e3)}}),document.addEventListener("DOMContentLoaded",function(){var elems=document.querySelectorAll(".sidenav");M.Sidenav.init(elems,{})});